	private import ScalarValues::*;

	part def Driver;
	part def Passenger;

	action def BoardVehicle {
		in driver : Driver;
		in passengers : Passenger[0..4];
	}

	action def OperateVehicle {
		in driver : Driver;
	}

	action def EvaluateFuelLevel {
		in fuelPercentage : Real;
	}

