		abstract occurrence situations : Situation[*] nonunique;
		occurrence def Cause :> Situation {
			attribute occurs[0..1]: Real;
		}
		abstract occurrence causes : Cause[*] nonunique;
		occurrence def FailureMode :> Situation {
